<?phpnamespace app\admin\controller;use app\admin\model\BannerModel;class BannerController extends  BaseController{    /**     * 列表页     * @return \think\response\Json|\think\response\View     */    public function index(){        $this->view->engine->layout(true);        if(\think\facade\Request::isAjax()){            $search = filter_array(input('','','trim'));            $data = BannerModel::getList($search);            return $data;        }        return view();    }    public function create(){        if(\think\facade\Request::isPost()){            try {                $data = input('post.','','trim');                $this->validate($data, 'Banner');                $banner = new BannerModel($data);                if(!$banner->save()){                    throw new \think\Exception('banner添加失败');                }                $result['msg'] = 'banner添加成功';                $result['success'] = true;                return json($result);            } catch (\Exception $e) {                return json(['success' => false, 'msg' => $e->getMessage(), 'data' => []]);            }        }    }    // 删除    public function delete()    {        try {            $id = input('id');            if(empty($id)){                throw new \think\Exception('错误请求');            }            $result = BannerModel::where(['id'=>$id])->delete();            if(!$result){                throw new \think\Exception('删除角色失败');            }            return json(['success' => 1, 'msg' => '删除成功']);        } catch (\Exception $e) {            return json(['success' => 0, 'msg' => $e->getMessage()]);        }    }}