<?phpnamespace app\admin\controller;use app\common\model\CompanyModel;use app\common\model\EnterCategoryModel;use think\Db;class CompanyController extends  BaseController{    /**     * 列表页     * @return \think\response\Json|\think\response\View     */    public function index(){        $this->view->engine->layout(true);        if(\think\facade\Request::isAjax()){            $data = array_filter(input('','','trim'));            $data=CompanyModel::getList($data);            return $data;        }        return view();    }    /**     * 添加     * @return \think\response\Json|\think\response\View     */    public function create(){        $this->view->engine->layout(true);        $category = EnterCategoryModel::getSelectArray();        if(\think\facade\Request::isPost()){            try {                Db::startTrans();                $data = input('','','trim');                $this->validate($data, 'Company.create');                $company = new CompanyModel($data);                if(!$company->save()){                    throw new \think\Exception('添加失败');                }                $result['msg'] = '操作成功';                $result['success'] = true;                $result['url'] = url('company/index');                Db::commit();                return json($result);            } catch (\Exception $e) {                Db::rollback();                return json(['success' => false, 'msg' => $e->getMessage(), 'data' => []]);            }        }        return view('',['category'=>$category]);    }    /**     * 添加     * @return \think\response\Json|\think\response\View     */    public function edit(){        $this->view->engine->layout(true);        $category = EnterCategoryModel::getSelectArray();        if(\think\facade\Request::isPost()){            try {                Db::startTrans();                $data = input('','','trim');                $company = CompanyModel::get($data['id']);                if(empty($company)){                    throw new \think\Exception('商户不存在');                }                $this->validate($data, 'Company');                if(!$company->force()->save( $data)){                    throw new \think\Exception('操作失败');                }                $result['msg'] = '操作成功';                $result['success'] = true;                $result['url'] = url('company/index');                Db::commit();                return json($result);            } catch (\Exception $e) {                Db::rollback();                return json(['success' => false, 'msg' => $e->getMessage(), 'data' => []]);            }        }        return view('',['category'=>$category]);    }    /**     * 展示     * @return \think\response\Json|\think\response\View     */    public function detail(){        $this->view->engine->layout(true);        $id = input('id');        try {            if(empty($id)){                throw new \think\Exception('错误请求');            }            $category = EnterCategoryModel::getSelectArray();            $data = CompanyModel::getDetail($id);            return  view('',['data'=>$data,'category'=>$category]);        } catch (\Exception $e) {            $this->view->engine->layout(false);            return  view('layout/message',['msg'=>$e->getMessage()]);        }    }//    /**//     * 删除//     * @return \think\response\Json//     *///    public function delete()//    {//        try {//            $id = input('id');//            if(empty($id)){//                throw new \think\Exception('错误请求');//            }//            $result = EnterCategoryModel::where(['id'=>$id])->delete();//            if(!$result){//                throw new \think\Exception('删除分类失败');//            }//            return json(['success' => 1, 'msg' => '删除成功']);//        } catch (\Exception $e) {//            return json(['success' => 0, 'msg' => $e->getMessage()]);//        }////    }}