<?phpnamespace app\admin\controller;use app\admin\model\NewsModel;use think\Db;class NewsController extends  BaseController{    /**     * 列表页     * @return \think\response\Json|\think\response\View     */    public function index(){        $this->view->engine->layout(true);        if(\think\facade\Request::isAjax()){            $data = array_filter(input('','','trim'));            $data=NewsModel::getList($data);            return $data;        }        return view();    }    /**     * 添加     * @return \think\response\Json|\think\response\View     */    public function create(){        $this->view->engine->layout(true);        if(\think\facade\Request::isPost()){            try {                Db::startTrans();                $data = input('post.','','trim');                $valiRes = $this->validate($data, 'News');                if ($valiRes !== true) {                    throw new \think\Exception($valiRes);                }                $result = saveData('news', $data);                if ($result['code'] == 0) {                    throw new \think\Exception('操作失败' . $result['msg']);                }                $result['msg'] = '操作成功';                $result['success'] = true;                Db::commit();                return json($result);            } catch (\Exception $e) {                Db::rollback();                c_Log($e);                return json(['success' => false, 'msg' => $e->getMessage(), 'data' => []]);            }        }        return view();    }    /**     * 编辑     * @return \think\response\Json|\think\response\View     */    public function edit(){        if(\think\facade\Request::isPost()){            try {                Db::startTrans();                $id = input('id');                if(empty($id)){                    throw new \think\Exception('错误请求');                }                $data = input('','','trim');                $valiRes = $this->validate($data, 'News');                if ($valiRes !== true) {                    throw new \think\Exception($valiRes);                }                $result = saveData('News', $data);                if ($result['code'] == 0) {                    throw new \think\Exception('编辑失败' . $result['msg']);                }                $result['msg'] = '编辑成功';                $result['success'] = true;                Db::commit();                return json($result);            } catch (\Exception $e) {                Db::rollback();                c_Log($e);                return json(['success' => false, 'msg' => $e->getMessage(), 'data' => []]);            }        }    }    /**     * 展示     * @return \think\response\Json|\think\response\View     */    public function show(){        $id = input('id');        try {            if(empty($id)){                throw new \think\Exception('错误请求');            }            $data= \app\common\model\News::getList(['id'=>$id]);            if($data['code'] == 0 ){                throw new \think\Exception($data['msg']);            }            if(empty($data['rows'])){                throw new \think\Exception('信息不存在');            }            return  view('',['data'=>$data['rows'][0]]);        } catch (\Exception $e) {            c_Log($e);            return  view('layout/message',['msg'=>$e->getMessage()]);        }    }    /**     * 设置banner     * @return \think\response\Json     */    public function banner()    {        try {            $id = input('id');            if(empty($id)){                throw new \think\Exception('错误请求');            }            $result = saveData('News', ['id'=>$id,'is_banner'=>1]);            if ($result['code'] == 0) {                throw new \think\Exception('设置失败');            }            return json(['code' => 1, 'msg' => '设置成功']);        } catch (\Exception $e) {            c_Log($e);            return json(['code' => 0, 'msg' => $e->getMessage()]);        }    }    /**     * 删除     * @return \think\response\Json     */    public function delete()    {        try {            $id = input('id');            if(empty($id)){                throw new \think\Exception('错误请求');            }            $delRes = deleteByCon('News', ['id'=>$id]);            if ($delRes['code'] == 0) {                throw new \think\Exception('删除失败');            }            return json(['code' => 1, 'msg' => '删除成功']);        } catch (\Exception $e) {            c_Log($e);            return json(['code' => 0, 'msg' => $e->getMessage()]);        }    }    /**     * 导出excel     */    public function export()    {        try {            $postData = array_filter(input('', null, 'trim'));            $postData['limit'] = 100000;            //查询条件            $list = \app\common\model\News::getList($postData);            if ($list['code'] != 1) {                throw new \think\Exception("查询失败:".$list['msg']);            }            vendor("PHPExcel");            vendor("PHPExcel.Writer.Excel2007");            vendor("PHPExcel.IOFactory");            //列名            $header = array('创建时间', '标题', '状态', '是否为banner');            if (!empty($list["rows"])) {                $name_list =[];                $field = ['add_date','title','status_alias','banner_alias'];                array_walk($list['rows'], function($value, $key) use (&$name_list ,$field){                    foreach ($field as $val){                        $name_list [$key][$val] = $value[$val];                    }                });                getExcel("新闻资讯表", $header, $name_list);            } else {                return view('layout/message',['msg'=>'没有相关数据']);            }        }catch(\Exception $e) {            c_Log($e);            return view('layout/message',['msg'=>$e->getMessage(),'code'=>$e->getCode()]);        }    }}