<?phpnamespace app\admin\controller;use app\common\model\CompanyModel;use app\common\model\EnterCategoryModel;use app\common\model\OrderAttrModel;use app\common\model\OrderModel;use app\common\model\ProductAttrModel;use app\common\model\ProductCategoryModel;use app\common\model\ProductModel;use think\Db;class OrderController extends  BaseController{    /**     * 列表页     * @return \think\response\Json|\think\response\View     */    public function index(){        $this->view->engine->layout(true);        if(\think\facade\Request::isAjax()){            $data = array_filter(input('','','trim'));            $data=OrderModel::getList($data);            return $data;        }        return view();    }    /**     * 展示     * @return \think\response\Json|\think\response\View     */    public function attr(){        $id = input('id');        try{            $attr = OrderAttrModel::getList(['order_id'=>$id]);            if($attr['code'] == 0){                throw new \Exception($attr['msg']);            }            foreach ($attr['rows'] as $k => &$v){//                $v['img_url'] = ' <a class="test-popup-link" href="'.$v['img'].'"   ><img class="table_img img-zoomable" src="'.$v['img'].'"></a>';                $v['img_url'] = ' <a  href="'.$v['img'].'"  target="_blank" ><img class="table_img img-zoomable" src="'.$v['img'].'"></a>';            }            return $attr['rows'];        }catch(\Exception $e) {            return json(['code' => 0, 'rows' => [], 'msg' => $e->getMessage()]);        }    }    /**     * 展示     * @return \think\response\Json|\think\response\View     */    public function detail(){        $this->view->engine->layout(true);        $id = input('id');        try{            $order = OrderModel::getList(['order_id'=>$id]);            if($order['code'] == 0){                throw new \Exception($order['msg']);            }            $order = $order['rows'][0]->toArray();            $attr = OrderAttrModel::getList(['order_id'=>$id]);            if($attr['code'] == 0){                throw new \Exception($attr['msg']);            }            $order['order_attr'] = $attr['rows']->toArray();            return view('',['data' => $order]);        }catch(\Exception $e) {            return json(['code' => 0, 'data' => [], 'msg' => $e->getMessage()]);        }    }}