<?phpnamespace app\admin\controller;use app\common\model\CompanyModel;use app\common\model\EnterCategoryModel;use app\common\model\OrderAttrModel;use app\common\model\OrderModel;use app\common\model\ProductAttrModel;use app\common\model\ProductCategoryModel;use app\common\model\ProductModel;use think\Db;class OrderController extends  BaseController{    /**     * 列表页     * @return \think\response\Json|\think\response\View     */    public function index(){        $this->view->engine->layout(true);        if(\think\facade\Request::isAjax()){            $data = array_filter(input('','','trim'));            $data=OrderModel::getList($data);            return $data;        }        return view();    }    /**     * 展示     * @return \think\response\Json|\think\response\View     */    public function attr(){        $id = input('id');        try{            $attr = OrderAttrModel::getList(['order_id'=>$id]);            if($attr['code'] == 0){                throw new \Exception($attr['msg']);            }            return $attr['rows'];        }catch(\Exception $e) {            return json(['code' => 0, 'rows' => [], 'msg' => $e->getMessage()]);        }    }    /**     * 展示     * @return \think\response\Json|\think\response\View     */    public function detail(){        $id = input('id');        try{            $attr = OrderAttrModel::getList(['order_id'=>$id]);            if($attr['code'] == 0){                throw new \Exception($attr['msg']);            }            return $attr['rows'];        }catch(\Exception $e) {            return json(['code' => 0, 'rows' => [], 'msg' => $e->getMessage()]);        }    }}