<?phpnamespace app\admin\controller;use app\common\model\ProductCategoryModel;class ProductCategoryController extends  BaseController{    /**     * 列表页     * @return \think\response\Json|\think\response\View     */    public function index()    {        $this->view->engine->layout(true);        $data = input('','','trim');        $search = [];        if(!empty($data)){            $search = filter_array($data);        }        $tree = ProductCategoryModel::tree($search);        if ($tree['code'] == 1) {            $tree_view = ProductCategoryModel::treeView($tree['data']);            $alias = ProductCategoryModel::$alia;            return view('', ['data' => $tree_view,'alias'=>$alias,'tree'=>$tree['data']]);        } else {            return view('layout/message', ['data' => $tree['msg']]);        }    }    /**     * 创建     * @return \think\response\Json|\think\response\View     */    public function create(){        if(\think\facade\Request::isPost()){            try {                $data = input('post.','','trim');                if(!empty($data['parent_id'])){                    $parent = ProductCategoryModel::get($data['parent_id']);                    if(empty($parent)){                        throw new \think\Exception('父级类别不存在');                    }                    $data['level'] = $parent->level +1;                }                $this->validate($data, 'ProductCategory');                $category = new ProductCategoryModel($data);                if(!$category->save()){                    throw new \think\Exception('分类添加失败');                }                $result['msg'] = '分类添加成功';                $result['success'] = true;                return json($result);            } catch (\Exception $e) {                return json(['success' => false, 'msg' => $e->getMessage(), 'data' => []]);            }        }        return view('');    }    /**     * 展示     * @return \think\response\Json|\think\response\View     */    public function edit(){        $id = input('id');        try {            if(empty($id)){                throw new \think\Exception('错误请求');            }            $category = ProductCategoryModel::get($id);            if(empty($category)){                throw new \think\Exception('分类不存在');            }            $tree = ProductCategoryModel::tree([]);            if($tree['code'] == 0){                throw new \think\Exception('分类列表失败');            }            $tree_view = ProductCategoryModel::treeView($tree['data']);            if(\think\facade\Request::isPost()){                try {                    $data = input('','','trim');                    $this->validate($data, 'ProductCategory');                    if(!$category->force()->save( $data)){                        throw new \think\Exception('编辑失败');                    }                    $result['msg'] = '编辑成功';                    $result['success'] = true;                    return json($result);                } catch (\Exception $e) {                    return json(['success' => false, 'msg' => $e->getMessage(), 'data' => []]);                }            }else{                return  view('',['data'=>$category,'tree'=>$tree_view]);            }        } catch (\Exception $e) {            return  view('layout/message',['msg'=>$e->getMessage()]);        }    }}