<?phpnamespace app\admin\controller;use app\common\model\VipPayLogModel;class VipPayLogController extends  BaseController{    /**     * 列表页     * @return \think\response\Json|\think\response\View     */    public function index(){        $this->view->engine->layout(true);        if(\think\facade\Request::isAjax()){            $search = filter_array(input('','','trim'));            $data = VipPayLogModel::getList($search);            return $data;        }        return view('');    }}