<?phpnamespace app\admin\model;use app\common\model\BaseModel;use app\common\model\SysFileModel;class FrontendMessageCategoryModel extends BaseModel{    public function getIsShowAttr($value,$data)    {        $status = [1=>'是',2=>'否'];        return $status[$data['is_show']];    }    /**     * 获取下拉列表需要的用户信息     * @return array     */    public static function getSelectArray(){        try {            $user = self::field(['id','name'])->order(['id'=>'desc'])->select();            $name_list = [];            foreach ($user as $k => $v){                $name_list[$v['id']] = $v['name'];            }            return $name_list;        }catch (Exception $e){            //记录错误日志            return [];        }    }    public static function getList($input){        try{            $default = [                'sort'=>'id',                'order'=>'desc',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $category = self::field('*')->with('sys_file');            if(isset($input['name'])){                $category->whereLike('name',"%".$input['name']."%");            }            $category->order($input['sort'],$input['order']);            $category->limit($input['offset'],$input['limit']);            $rows = $category->select();            $count = $category->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    public function sysFile()    {        return $this->hasOne(SysFileModel::class,'id','file_id');    }}