<?phpnamespace app\admin\model;use app\common\model\BaseModel;use app\common\model\SysFileModel;class FrontendMessageModel extends BaseModel{    public static function getList($input){        try{            $default = [                'sort'=>'id',                'order'=>'desc',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $message = self::field('*')->with(['sys_file','frontend_message_category']);            if(isset($input['info'])){                $message->whereLike('title|linkman|mobile',"%".$input['info']."%");            }            if(isset($input['category_id'])){                $message->where(['category_id'=>$input['category_id']]);            }            $message->order($input['sort'],$input['order']);            $message->limit($input['offset'],$input['limit']);            $rows = $message->select();            $count = $message->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    public function sysFile()    {        return $this->hasOne(SysFileModel::class,'id','img')->bind(['save_path']);    }    public function frontendMessageCategory()    {        return $this->hasOne(FrontendMessageCategoryModel::class,'id','category_id')->bind([            'category_name'=>'name',        ]);    }}