<?phpnamespace app\admin\model;use app\common\model\BaseModel;use app\common\model\SysFileModel;class FrontendMessageModel extends BaseModel{//    public function getTypeAttr($value,$data)//    {//        $status = [1=>'图片',2=>'视频'];//        return $status[$data['type']];//    }    public static function getList($input){        try{            $default = [                'sort'=>'id',                'order'=>'desc',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $banner = self::field('*')->with('sys_file');            $banner->order($input['sort'],$input['order']);            $banner->limit($input['offset'],$input['limit']);            $rows = $banner->select();            foreach ($rows as $k => &$v){                $v['link_alias'] = $v['link_url']?'<a  target="_blank" href="'.$v['link_url'].'">'.$v['link_url'].'</a>':'';                $v['download'] = '';                if($v->sys_file != null && check_file_exits($v->sys_file->save_path)){                    $v['download'] = '<a  target="_blank" href="'.$v->sys_file->save_path.'">'.$v->sys_file['name'].'</a>';                }            }            $count = $banner->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    public function sysFile()    {        return $this->hasOne(SysFileModel::class,'id','file_id');    }}