<?phpnamespace app\common;class ButtonElement{    public  static function confirmButton($url,$message = '',$name = '删除',$type = 'dark',$icon = 'wb-trash',$extension=''){        $str = '<button type="button" class="btn btn-outline btn-'.$type.' btn-sm" data-toggle="confirm" '.$extension;        if(!empty($url)){            $str .= ' data-url="'.$url.'"';        }        if(!empty($message)){            $str .= ' data-message="'.$message.'"';        }        $str .= '>';        $str .= '<i class="icon '.$icon.'" aria-hidden="true"></i> '.$name;        $str .=  '</button> ';        return $str;    }    public  static function showLink($url,$name = '查看',$type = 'success',$extension =''){        $str = '<a href='.$url.' class="btn btn-outline btn-'.$type.' btn-sm" '.$extension.'>'.'<i class="icon wb-file" aria-hidden="true"></i>'.$name.'</a>';        return $str;    }}