<?phpnamespace app\common\model;class CartModel extends BaseModel{    public static function getList($input){        try{            $default = [                'sort'=>'id',                'order'=>'desc',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $cart = self::field('a.*')->alias('a');            if(isset($input['id'])){                $cart->where('id',"=",$input['id']);            }            $cart->limit($input['offset'],$input['limit']);            $rows = $cart->select();            $count = $rows->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    public static function countByCustomer($customer_id){        return CartModel::where(['customer_id'=>$customer_id])->sum('count');    }}