<?phpnamespace app\common\model;class OrderAttrModel extends BaseModel{    public static function getList($input){        try{            $default = [                'sort'=>'id',                'order'=>'desc',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $attr = self::field('*')->with(['img_file']);            if(isset($input['order_id'])){                $attr->where('order_id',"=",$input['order_id']);            }            $attr->order($input['sort'],$input['order']);            $attr->limit($input['offset'],$input['limit']);            $rows = $attr->select();            $count = $attr->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    public function imgFile()    {        return $this->hasOne(SysFileModel::class,'id','img')->bind([            'img_path'=>'save_path',        ]);    }}