<?phpnamespace app\common\model;use app\common\ButtonElement;class OrderModel extends BaseModel{    public function getStatusAttr($value,$data)    {        $status = [1=>'待支付',2=>'已支付',3=>'待处理',4=>'处理中',5=>'已完成'];        return $status[$data['status']];    }    public function getPayTypeAttr($value,$data)    {        $status = [1=>'支付宝',2=>'微信'];        return $status[$data['pay_type']];    }    public function getOperateAttr($value,$data){        $operation =  [            'show'=>ButtonElement::showLink(url('admin/order/detail',['id'=>$data['id']])),        ];        return $operation;    }    public static function getList($input){        try{            $default = [                'sort'=>'id',                'order'=>'desc',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $product = self::field('*')->with(['customer']);            if(isset($input['id'])){                $product->where('id',"=",$input['id']);            }            if(isset($input['info'])){                $product->whereLike('name|label',"%".$input['info']."%");            }            $product->order($input['sort'],$input['order']);            $product->limit($input['offset'],$input['limit']);            $rows = $product->select();            foreach ($rows as $k => &$v){               $v['operation'] = implode(' ',$v->operate);            }            $count = $product->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    public function customer()    {        return $this->hasOne(CustomerModel::class,'id','customer_id')->bind([            'customer_mobile'=>'mobile',        ]);    }}