<?phpnamespace app\common\model;class ProductAttrModel extends BaseModel{    public static function getList($input){        try{            $default = [                'sort'=>'',                'order'=>'',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $product = self::field('*')->with(['product_category','company']);            if(isset($input['product_id'])){                $product->where('product_id',"=",$input['product_id']);            }            $product->order($input['sort'],$input['order']);            $product->limit($input['offset'],$input['limit']);            $rows = $product->select();            $count = $product->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    public function productCategory()    {        return $this->hasOne(ProductCategoryAttrModel::class,'id','category_attr_id')->bind([            'category_attr_name'=>'name',        ]);    }    public function company()    {        return $this->hasOne(ProductModel::class,'id','product_id')->bind([            'product_name'=>'name',        ]);    }}