<?phpnamespace app\common\model;class RegionModel extends BaseModel{    /**     * 根据ids获取省市区的对应名称     * author: zzx     * Date: 2018/12/24 0024     * Time: 14:13     * @param array $ids     * @return array     */    public static function nameArrayByIds($ids = []){        $data = self::whereIn('id',$ids)->order('id asc')->column('name');        return $data?:['','',''];    }}