<?phpnamespace app\common\model;class VipPayLogModel extends BaseModel{    /**     * 定义别名字段     * @var array     */    public static $alia = [        'pay_type'=>[            1=>'微信',            2=>'支付宝',            3=>'其他',        ],    ];    public function getPayTypeAttr($value)    {        return isset(self::$alia['pay_type'])?self::$alia['pay_type'][$value]:'';    }    /**     *获取列表信息     * @param $input     * @param string $field     * @return array     */    public static function getList($input,$field=''){        try{            $default = [                'sort'=>'id',                'order'=>'desc',                'offset'=>0,                'limit'=>null,            ];            $input = array_merge($default,$input);            $field = !empty($field)?$field:'*';            $customer = self::field($field);            if(isset($input['customer_id'])){                $customer->where(['customer_id'=>$input['customer_id']]);            }            $customer->order($input['sort'],$input['order']);            $customer->limit($input['offset'],$input['limit']);            $rows = $customer->select();            $count = $customer->count();            return ['code'=>1,'total' => $count,'rows'=>$rows];        }catch(\Exception $e) {            return array('code' => 0, 'rows' => [], 'msg' => $e->getMessage());        }    }    }