<?phpnamespace app\common\validate;use think\Validate;class UserValidate extends Validate{	protected $rule = [        'name|用户名'  => 'require|unique:user,name',        'mobile|手机号码'   => 'require|mobile|unique:user,mobile',        'password|登录密码' => 'require|min:6',        'repassword|重复密码' => 'require|min:6|confirm:password',        'oldpassword|原始密码' => 'require|min:6',        'newpassword|新密码' => 'require|min:6',        'confirm|确认密码' => 'require|min:6|confirm:newpassword',        'role_id|用户角色'=>'require',        'status|状态'=>'require',        'code|验证码'=>'require',    ];	protected $scene = [			'login'  =>  ['mobile'=>'require|mobile','password'],			'forget'  =>  ['mobile'=>'require|mobile','password','repassword','code'],			'add'  =>  ['name','mobile','role_id','status','mobile'],            'edit'  =>  ['name'=>'require|unique:user,name^id','mobile','role_id','status','mobile'=>'require|mobile|unique:user,mobile^id'],//            'mobile'  =>  ['mobile'=>'require|checkMobile|uniqueMob'],//            'changePassword'  =>  ['oldpassword','newpassword','confirm'],	];}